import React from "react";
import Switch from "react-switch";
class ProductCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    var { item } = this.props;

    return (
      <div className="card" key={item.id}>
        <div className="card-image">
          <img src={item.img} alt={item.nameEng} />
        </div>

        <div className="card-content">
          <div className="card-details">
            <div className="card-dep">
              {item.activation === "true" ? "Active" : "Not Active"}
            </div>
            <div className="card-title">{item.nameEng}</div>
            <div className="card-title">{item.nameArb}</div>
          </div>
          <div className="card-price">{item.price}$</div>
        </div>
        <div className="editDelete">
          <Switch
            onChange={() =>
              this.props.onActivateDeActivate(item.id, item.activation)
            }
            checked={item.activation}
          />

          <div
            className="centerLeftRight"
            onClick={() => this.props.onEditItem(item.id)}
          >
            edit
          </div>
          <div
            className="centerLeftRight"
            onClick={() => this.props.onDeletePizza(item.id)}
            style={{ color: "red" }}
          >
            delete
          </div>
        </div>
      </div>
    );
  }
}

export default ProductCard;
