import Item1 from "../../images/item1.jpg";
import Item2 from "../../images/item2.jpg";
import Item3 from "../../images/item3.jpg";
import Item4 from "../../images/item4.jpg";
import Item5 from "../../images/item5.jpg";
import Item6 from "../../images/item6.jpg";

import {
  CHANGE_FILTER,
  ADD_PIZZA,
  EDIT_PIZZA,
  DELETE_PIZZA,
  ON_INIT_FUNCTION,
} from "../actions/action-types/pizza-actions";

const initState = {
  items: [
    {
      id: 1,
      activeDiscount: true,
      discount: 5,
      nameEng: "pepperoni pizza",
      nameArb: "جزمه1",
      desc: "we just having nice pizza with peproni and cheese",
      price: 110,
      img: Item1,
      type: "main course",
    },
    {
      id: 2,
      activeDiscount: false,
      nameEng: "margherita pizza",
      discount: 10,
      desc: "it's just a nomral pizza ",
      price: 80,
      img: Item2,
      type: "main course",
    },
    {
      id: 3,
      activeDiscount: true,
      discount: 10,
      nameEng: "bbq chicken pizza",
      desc: "Nice Pizza with wonderfull grilled Chicken",
      price: 120,
      img: Item3,
      type: "side course",
    },
    {
      id: 4,
      activeDiscount: false,
      discount: 10,
      nameEng: "hawaiian pizza",
      desc: "wooow, very delecious pizza with pinapples",
      price: 260,
      img: Item4,
      type: "side course",
    },
    {
      id: 5,
      activeDiscount: true,
      discount: 10,
      nameEng: "cheese lovers pizza",
      desc: "enjoy more than 4 types of melted cheese in the same pizza",
      price: 160,
      img: Item5,
      type: "side course",
    },
    {
      id: 6,
      activeDiscount: false,
      discount: 10,
      nameEng: "sweet and chilli pizza",
      desc: "Sweet but spicy pizza, enjoy the multiple flavours in one pizza",
      price: 90,
      img: Item6,
      type: "main course",
    },
    {
      id: 7,
      activeDiscount: false,
      discount: 10,
      nameEng: "stuffed crust pizza",
      desc: "enjoy every bite of your pizza even its edges",
      price: 90,
      img: Item1,
      type: "side course",
    },
  ],
  addedItems: [],
  fitlteredItems: [],
  total: 0,
};
const cartReducer = (state = initState, action) => {
  switch (action.type) {
    case ON_INIT_FUNCTION:
      return {
        ...state,
        items: state.items,
      };

    case CHANGE_FILTER:
      return {
        ...state,
        fitlteredItems: action.filteredProducts,
      };
    case ADD_PIZZA: {
      return {
        ...state,
        items: action.items,
      };
    }
    case EDIT_PIZZA: {
      return {
        ...state,
        items: action.items,
      };
    }
    case DELETE_PIZZA: {
      return { ...state, items: action.items };
    }

    default:
      return state;
  }
};

export default cartReducer;
